const mongoose = require('mongoose');

const analysisSchema = new mongoose.Schema(
  {
    // Link to the user who requested the analysis
    user: {
      type: mongoose.Schema.Types.ObjectId,
      ref: "User",
      required: true,
    },
    // The original text extracted from the image by the AI (OCR result)
    originalText: {
      type: String,
      required: true,
    },
    // The simplified explanation generated by the AI for the patient
    simplifiedText: {
      type: String,
      required: true,
    },
    // The type of document that was analyzed
    documentType: {
      type: String,
      enum: ["Prescription", "Lab Report", "Other"],
      default: "Other",
    },
  },
  {
    timestamps: true,
  }
);

module.exports = mongoose.model("Analysis", analysisSchema);